mapper.select:
SELECT p.*, c.name as category_name, u.username as seller_username, u.realname as seller_realname, u.phone as seller_phone, u.company_name as seller_company_name FROM product p LEFT JOIN category c ON p.category_id = c.id LEFT JOIN user u ON p.seller_id = u.id
AND p.category_id = #{categoryId}
AND p.seller_id = #{sellerId}
AND p.status = #{status}
AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.brand LIKE CONCAT('%', #{keyword}, '%') OR p.model LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY p.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM product p


        AND p.category_id = #{categoryId}


        AND p.seller_id = #{sellerId}


        AND p.status = #{status}


        AND (p.name LIKE CONCAT('%', #{keyword}, '%') 
             OR p.brand LIKE CONCAT('%', #{keyword}, '%')
             OR p.model LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE product
name = #{name}, category_id = #{categoryId}, price = #{price}, unit = #{unit}, stock = #{stock}, brand = #{brand}, model = #{model}, specifications = #{specifications}, description = #{description}, main_image = #{mainImage}, images = #{images}, status = #{status},
WHERE id = #{id}
